package br.com.elvisassis;import static br.com.elvisassis.TipoConta.*;public class ContaFactory {  public static IConta criarConta(int tipoConta, Cliente cliente, double valor) {    switch (TipoConta.fromInt(tipoConta)) {      case CORRENTE -> {        return new ContaCorrente(cliente, valor);      }      case POUPANCA -> {        return new ContaPoupanca(cliente, valor);      }      default -> {        return null;      }    }  }}