package br.com.elvisassis;import br.com.elvisassis.exception.TipoContaInvalidaException;public enum TipoConta {    CORRENTE,    POUPANCA;    public static TipoConta fromInt(int i) {        switch (i) {            case 0: return TipoConta.CORRENTE;            case 1: return TipoConta.POUPANCA;            default: throw new TipoContaInvalidaException();        }    }}