package br.com.elvisassis;import br.com.elvisassis.exception.CepInvalidException;public class FormatadorCepExemplo {    public static void main(String[] args) {        try {            String cepFormatado = formatarCep("23765015");            System.out.println("Cep formatado: " + cepFormatado);        } catch (CepInvalidException e) {            System.out.println("O cep não corresponde com as regras de negócio!");        }    }    static String formatarCep(String cep) throws CepInvalidException {        if(cep.length() != 8)            throw new CepInvalidException();        return "23.765.005";    }}